// Anonymous campaign setup
// Select 20% of employees randomly
const employees = items;
const campaignId = 'CAMP_' + Date.now();
const sampleSize = Math.ceil(employees.length * 0.2);

// Shuffle and select
const shuffled = employees.sort(() => 0.5 - Math.random());
const selected = shuffled.slice(0, sampleSize);

// Create anonymous tracking URLs
return selected.map(item => {
 // Generate unique ID that can't be traced back
 const uniqueId = Math.random().toString(36).substring(2, 15);
 const department = item.json.department || 'general';
 const template = Math.floor(Math.random() * 3) + 1;

// Build anonymous tracking URL
const webhookId = '5881355f-1a60-4511-bebf-158468b83780';  // From your webhook node
const baseUrl = $env["WEBHOOK_URL"] || `${$env["N8N_PROTOCOL"]}://{$env["N8N_HOST"]}:${$env["N8N_PORT"]}/`;
const trackingUrl = `${baseUrl}webhook/${webhookId}/phishing/${uniqueId}/${department}/${template}`;

 return {
 json: {
 email: item.json.email,
 name: item.json.name || 'Team Member',
 department: department,
 trackingUrl: trackingUrl,
 template: template,
 campaignId: campaignId,
 timestamp: new Date().toISOString()
 }
 };
});

// Calculate anonymous metrics
const stats = items;
const oneWeekAgo = new Date();
oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);

// Filter this week's data
const thisWeek = stats.filter(item => {
  const date = new Date(item.json.Timestamp);
  return date > oneWeekAgo;
});

// Separate sent vs clicked
const sent = thisWeek.filter(s => s.json.Status === 'Sent');
const clicked = thisWeek.filter(s => s.json.Status === 'Clicked');

// Department analysis
const deptStats = {};
sent.forEach(s => {
  const dept = s.json.Department;
  if (!deptStats[dept]) deptStats[dept] = { sent: 0, clicked: 0 };
  deptStats[dept].sent++;
});

clicked.forEach(c => {
  const dept = c.json.Department;
  if (!deptStats[dept]) deptStats[dept] = { sent: 0, clicked: 0 };
  deptStats[dept].clicked++;
});

// Calculate department click rates
const deptPerformance = Object.entries(deptStats).map(([dept, stats]) => ({
  department: dept,
  sent: stats.sent,
  clicked: stats.clicked,
  rate: ((stats.clicked / stats.sent) * 100).toFixed(1) + '%'
})).sort((a, b) => parseFloat(b.rate) - parseFloat(a.rate));

// Template effectiveness
const templateStats = {};
clicked.forEach(c => {
  const template = c.json.Template;
  templateStats[template] = (templateStats[template] || 0) + 1;
});

const overallClickRate = sent.length === 0 ? 0 : ((clicked.length / sent.length) * 100).toFixed(1);  // Avoid NaN

return [{
  json: {
    reportDate: new Date().toISOString(),
    period: 'Last 7 Days',
    totalSent: sent.length,
    totalClicks: clicked.length,
    clickRate: overallClickRate + '%',
    departmentStats: deptPerformance,
    templateEffectiveness: {
      passwordExpiry: templateStats['1'] || 0,
      sharedDocument: templateStats['2'] || 0,
      securityAlert: templateStats['3'] || 0
    },
    riskLevel: overallClickRate > 25 ? 'HIGH' : overallClickRate > 10 ? 'MEDIUM' : 'LOW',
    trend: overallClickRate > 15 ? 'üìà Needs Improvement' : 'üìâ Improving',
    recommendations: overallClickRate > 25 
      ? ['Schedule all-hands security training', 'Increase awareness communications', 'Focus on ' + (deptPerformance[0]?.department || 'N/A') + ' department']
      : overallClickRate > 10
      ? ['Continue regular training', 'Share success stories', 'Target high-risk departments']
      : ['Maintain current program', 'Consider advanced scenarios', 'Celebrate improvement']
  }
}];

// Format report for Slack/Email
const metrics = $json;
const riskEmoji = metrics.riskLevel === 'HIGH' ? 'üî¥' : metrics.riskLevel === 'MEDIUM' ? 'üü°' : 'üü¢';

// Create department leaderboard (safe for empty array)
const deptLeaderboard = (metrics.departmentStats || [])
  .slice(0, 5)
  .map((d, i) => `${i + 1}. ${d.department}: ${d.rate} (${d.clicked}/${d.sent} clicked)`)
  .join('\n');

// Template effectiveness
const templateReport = `
üéØ Most Effective Phishing Types:
‚Ä¢ Password Expiry: ${metrics.templateEffectiveness?.passwordExpiry || 0} clicks
‚Ä¢ Shared Document: ${metrics.templateEffectiveness?.sharedDocument || 0} clicks 
‚Ä¢ Security Alert: ${metrics.templateEffectiveness?.securityAlert || 0} clicks
`;

const slackMessage = `
${riskEmoji} *Weekly Phishing Defense Report*
‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ

üìä *Overall Statistics*
‚Ä¢ Phishing Tests Sent: ${metrics.totalSent}
‚Ä¢ Clicks Recorded: ${metrics.totalClicks}
‚Ä¢ Click Rate: *${metrics.clickRate}*
‚Ä¢ Risk Level: *${metrics.riskLevel}*
‚Ä¢ Trend: ${metrics.trend}

üèÜ *Department Performance*
${deptLeaderboard}

${templateReport}

üí° *Recommendations*
${(metrics.recommendations || []).map(r => '‚Ä¢ ' + r).join('\n')}

_Note: All tracking is anonymous. No individual employee data is collected._

üìà Full dashboard: company.com/security-metrics
`;

const emailHTML = `
<div style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; max-width: 600px; margin: 0 auto;">
 <div style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; border-radius: 10px 10px 0 0;">
 <h1>${riskEmoji} Weekly Security Report</h1>
 <p style="opacity: 0.9;">Anonymous Phishing Defense Metrics</p>
 </div>

 <div style="padding: 30px; background: #f8f9fa;">
 <div style="background: white; padding: 20px; border-radius: 8px; margin-bottom: 20px;">
 <h3>üìä Key Metrics</h3>
 <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 15px;">
 <div style="padding: 10px; background: #f1f3f4; border-radius: 4px;">
 <div style="font-size: 24px; font-weight: bold; color: #1a73e8;">${metrics.clickRate}</div>
 <div style="font-size: 12px; color: #666;">Click Rate</div>
 </div>
 <div style="padding: 10px; background: #f1f3f4; border-radius: 4px;">
 <div style="font-size: 24px; font-weight: bold; color: ${metrics.riskLevel === 'HIGH' ? '#d93025' : metrics.riskLevel === 'MEDIUM' ? '#fbbc04' : '#34a853'};">${metrics.riskLevel}</div>
 <div style="font-size: 12px; color: #666;">Risk Level</div>
 </div>
 </div>
 </div>

 <div style="background: white; padding: 20px; border-radius: 8px;">
 <h3>üèÜ Department Leaderboard</h3>
 ${(metrics.departmentStats || []).map((d, i) => `
 <div style="padding: 10px; border-bottom: 1px solid #f1f3f4;">
 <strong>${i + 1}. ${d.department}</strong>: ${d.rate} click rate
 </div>
 `).join('')}
 </div>
 </div>

 <div style="background: #343a40; color: white; padding: 20px; text-align: center; border-radius: 0 0 10px 10px;">
 <p style="margin: 5px; font-size: 12px;">All metrics are anonymous. No individual tracking performed.</p>
 </div>
</div>
