// FORMAT ALERT
const item = $input.item.json;
const emoji = { critical: 'üö®', high: '‚ö†Ô∏è', medium: 'üîç', low: 'üìù' };

let msg = `${emoji[item.threatLevel]} **${item.threatLevel.toUpperCase()} THREAT**\nü¶ã **Platform:** BLUESKY\n\n`;
msg += `**Account:** @${item.author}\n**Followers:** ${item.authorFollowers} | Age: ${item.accountAge}d\n`;
msg += `**Engagement:** ${item.retweetCount + item.favoriteCount}\n**Threat Score:** ${item.threatScore}/100\n\n`;

if (item.redFlags.length > 0) {
  msg += `**üö© Red Flags:**\n${item.redFlags.map(f => `‚Ä¢ ${f}`).join('\n')}\n\n`;
}

if (item.volumeData.campaignDetected) {
  msg += `**üéØ CAMPAIGN: ${item.volumeData.campaignReason}**\n\n`;
}

msg += `**Content:**\n> ${item.fullText.substring(0, 280)}\n\n**Link:** ${item.url}\n\n`;
msg += `_Activity: ${item.volumeData.hourlyCount} posts, ${item.volumeData.hourlySuspicious} suspicious_`;

return {
  json: {
    ...item,
    alertMessage: msg,
    slackChannel: item.threatLevel === 'critical' ? 'alerts-critical' : 'alerts-general'
  }
};

// VOLUME PATTERN & CAMPAIGN DETECTION
const staticData = $getWorkflowStaticData('global');
const now = new Date();
const hourKey = now.getHours();

if (!staticData.hourlyData) staticData.hourlyData = {};
if (!staticData.hourlyData[hourKey]) {
  staticData.hourlyData[hourKey] = { count: 0, suspicious: 0, accounts: [] };
}

const results = [];
const currentHour = staticData.hourlyData[hourKey];
const allItems = $input.all();

allItems.forEach(item => {
  currentHour.count++;
  if (['high', 'critical'].includes(item.json.threatLevel)) currentHour.suspicious++;

  const accountKey = `bluesky:${item.json.author}`;
  if (!currentHour.accounts.includes(accountKey)) currentHour.accounts.push(accountKey);
});

let campaignDetected = false;
let campaignReason = '';

if (currentHour.suspicious >= 5) {
  campaignDetected = true;
  campaignReason = `${currentHour.suspicious} suspicious posts this hour`;
}

const previousHour = staticData.hourlyData[(hourKey - 1 + 24) % 24];
if (previousHour && currentHour.count > previousHour.count * 3) {
  campaignDetected = true;
  campaignReason = `3x spike vs previous hour (${currentHour.count} vs ${previousHour.count})`;
}

const newAccounts = allItems.filter(i => i.json.accountAge < 30).length;
if (newAccounts >= 3) {
  campaignDetected = true;
  campaignReason = `${newAccounts} new accounts posting`;
}

allItems.forEach(item => {
  results.push({
    json: {
      ...item.json,
      volumeData: {
        hourlyCount: currentHour.count,
        hourlySuspicious: currentHour.suspicious,
        uniqueAccounts: currentHour.accounts.length,
        campaignDetected,
        campaignReason
      },
      shouldAlert: item.json.threatLevel !== 'low' || campaignDetected
    }
  });
});

return results;

// CALCULATE TOTAL THREAT
const results = [];

for (const item of $input.all()) {
  const data = item.json;
  const checks = data.checks || {};

  let totalScore = 0;
  const allFlags = [];

  Object.values(checks).forEach(check => {
    if (check.score) totalScore += check.score;
    if (check.flags) allFlags.push(...check.flags);
  });

  let threatLevel = 'low';
  let threatType = 'normal';

  if (totalScore >= 70) {
    threatLevel = 'critical';
    threatType = 'likely_campaign';
  } else if (totalScore >= 50) {
    threatLevel = 'high';
    threatType = 'suspicious';
  } else if (totalScore >= 30) {
    threatLevel = 'medium';
    threatType = 'monitor';
  }

  results.push({
    json: {
      ...data,
      threatScore: totalScore,
      redFlags: allFlags,
      threatLevel,
      threatType,
      timestamp: new Date().toISOString()
    }
  });
}

return results;

// BOT DETECTION (+95 points max)
const results = [];

for (const item of $input.all()) {
  const data = item.json;
  const accountAge = data.accountAge || 0;
  let score = 0;
  const flags = [];

  if (accountAge < 30 && data.authorTweets > 500) {
    score += 30;
    flags.push(`Bot: New account (${accountAge}d) with ${data.authorTweets} posts`);
  }

  if (/[0-9]{6,}$/.test(data.author)) {
    score += 20;
    flags.push('Bot: Username ends with 6+ numbers');
  }

  const ratio = data.authorFollowers / (data.authorFriends || 1);
  if (ratio < 0.1 && data.authorFollowers < 50) {
    score += 20;
    flags.push(`Bot: Low follower ratio ${ratio.toFixed(2)}`);
  }

  const engagement = data.retweetCount + data.favoriteCount;
  if (data.authorFollowers < 100 && engagement > 500) {
    score += 25;
    flags.push('Bot: Suspicious engagement');
  }

  results.push({
    json: {
      ...data,
      checks: {
        ...data.checks,
        bot: { score, flags, accountAge }
      }
    }
  });
}

return results;

// BEHAVIORAL TACTICS (+25 points max)
const results = [];

for (const item of $input.all()) {
  const data = item.json;
  let score = 0;
  const flags = [];

  const hashtags = (data.text.match(/#/g) || []).length;
  if (hashtags >= 5) {
    score += 15;
    flags.push(`Hashtag stuffing: ${hashtags} tags`);
  }

  const capsWords = data.fullText.match(/\b[A-Z]{4,}\b/g) || [];
  if (capsWords.length >= 3) {
    score += 10;
    flags.push(`Excessive CAPS: ${capsWords.length} words`);
  }

  results.push({
    json: {
      ...data,
      checks: {
        ...data.checks,
        behavioral: { score, flags }
      }
    }
  });
}

return results;

// CALL-TO-ACTION (+20 points)
const ctaPhrases = ['share this','retweet','rt this','boost this','spread the word','everyone needs','must see','wake up','open your eyes','dont let them','before they delete','they dont want you','tag someone','share if you agree','pass it on','viral','make this go viral'];

const results = [];

for (const item of $input.all()) {
  const data = item.json;
  let score = 0;
  const flags = [];
  let count = 0;
  const found = [];

  ctaPhrases.forEach(w => {
    if (data.text.includes(w)) {
      count++;
      found.push(w);
    }
  });

  if (count >= 1) {
    score += 20;
    flags.push(`Call-to-action: "${found[0]}"`);
  }

  results.push({
    json: {
      ...data,
      checks: {
        ...data.checks,
        cta: { score, flags }
      }
    }
  });
}

return results;

// HEDGE WORDS (+15 points)
const hedgeWords = ['allegedly','reportedly','supposedly','rumor','rumors','sources say','sources claim','heard that','they say','according to','claims that','suggests that','could be','might be','may have','possibly','probably','apparently','seemingly'];

const results = [];

for (const item of $input.all()) {
  const data = item.json;
  let score = 0;
  const flags = [];
  let count = 0;
  const found = [];

  hedgeWords.forEach(w => {
    if (data.text.includes(w)) {
      count++;
      found.push(w);
    }
  });

  if (count >= 1) {
    score += 15;
    flags.push(`Unverified language: "${found[0]}"`);
  }

  results.push({
    json: {
      ...data,
      checks: {
        ...data.checks,
        hedges: { score, flags }
      }
    }
  });
}

return results;

// EMOTIONAL MANIPULATION (+25 points)
const emotionalWords = ['shocking','terrifying','alarming','dangerous','threat','crisis','panic','scared','afraid','horror','terror','frightening','outrageous','disgusting','furious','enraged','angry','hate','rage','hostile','violent','attack','destroy','scandal','corrupt','evil','traitor','criminal','betrayal','enemy','fraud','scam','lie','liar','fake','breaking','urgent','emergency','alert','warning','exposed','leaked','revealed','bombshell','explosive'];

const results = [];

for (const item of $input.all()) {
  const data = item.json;
  let score = 0;
  const flags = [];
  let count = 0;
  const found = [];

  emotionalWords.forEach(w => {
    if (data.text.includes(w)) {
      count++;
      found.push(w);
    }
  });

  if (count >= 2) {
    score += 25;
    flags.push(`Emotional manipulation: ${count} triggers (${found.slice(0,3).join(', ')})`);
  }

  results.push({
    json: {
      ...data,
      checks: {
        ...data.checks,
        emotional: { score, flags, count }
      }
    }
  });
}

return results;

// Normalize Bluesky data to common format
const results = [];
const data = $input.first().json;

if (data.posts && Array.isArray(data.posts)) {
  for (const post of data.posts) {
    const author = post.author || {};
    const record = post.record || {};
    const text = (record.text || '').toLowerCase();

    const createdAt = new Date(author.createdAt || Date.now());
    const accountAge = Math.floor((Date.now() - createdAt.getTime()) / (1000 * 60 * 60 * 24));

    results.push({
      json: {
        platform: 'bluesky',
        id: post.uri,
        text: text,
        fullText: record.text || '',
        author: author.handle || 'unknown',
        authorFollowers: author.followersCount || 0,
        authorFriends: author.followsCount || 0,
        authorCreated: author.createdAt || new Date().toISOString(),
        authorTweets: author.postsCount || 0,
        retweetCount: post.repostCount || 0,
        favoriteCount: post.likeCount || 0,
        createdAt: post.indexedAt || new Date().toISOString(),
        url: `https://bsky.app/profile/${author.handle}/post/${post.uri.split('/').pop()}`,
        accountAge: accountAge,
        threatScore: 0,
        redFlags: [],
        checks: {}
      }
    });
  }
}

return results;
